mapper.select:
SELECT APPLY_NO, APPLY_ST, TEAM_NAME, ENTITY_STATUS, ESTABLISHED, HQ_LOCATION, CREATED_NO, REPRESENTATIVE, INDUSTRY, BUSINESS_ITEM, WEBSITE, SOCIAL_MEDIA_LINK, PROMO_VIDEO_LINK, CONVERT_TZ(CREATED_AT, 'UTC', 'Asia/Seoul') AS CREATED_AT, FN_CODE_NM(ENTITY_STATUS, 'NAME') AS ENTITY_STATUS_NAME, FN_CODE_NM(HQ_LOCATION, 'NAME') AS HQ_LOCATION_NAME, FN_CODE_NM(INDUSTRY, 'NAME') AS INDUSTRY_NAME FROM T_APPLY A WHERE 1=1 AND A.DELETED_FG = 'N'
AND (TEAM_NAME LIKE CONCAT('%', #{searchKeyword}, '%') OR BUSINESS_ITEM LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY A.CREATED_AT DESC LIMIT #{startRow}, #{pageCnt}

mapper.update:
UPDATE T_APPLY
SET
    TEAM_NAME = #{teamName}
  , ENTITY_STATUS = #{entityStatus}
  , ESTABLISHED = #{established}
  , HQ_LOCATION = #{hqLocation}
  , REPRESENTATIVE = #{representative}
  , INDUSTRY = #{industry}
  , BUSINESS_ITEM = #{businessItem}
  , WEBSITE = #{website}
  , SOCIAL_MEDIA_LINK = #{socialMediaLink}
  , PROMO_VIDEO_LINK = #{promoVideoLink}
  , SURVEY = #{survey}
  , UPDATED_AT = NOW()
WHERE
    APPLY_NO = #{applyNo}
