mapper.select:
SELECT A.BOARD_NO, A.CUSTOMER_NO, A.BOARD_TP, A.BOARD_SUBJECT, A.CREATED_AT, FN_USER_NM(A.CREATED_NO) AS CREATED_NAME, FN_FILE_YN(A.BOARD_NO, "B") AS BOARD_FILE_YN FROM T_BOARD A WHERE 1=1 AND A.BOARD_TP = #{boardTp}
AND A.CUSTOMER_NO = #{customerNo}
AND A.DELETED_FG = 'N'
AND (BOARD_SUBJECT LIKE CONCAT('%', #{searchKeyword}, '%') OR BOARD_CONTENT LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY A.CREATED_AT DESC LIMIT #{startRow}, #{pageCnt}

SELECT COUNT(*) AS CNT FROM (SELECT A.BOARD_NO, A.CUSTOMER_NO, A.BOARD_TP, A.BOARD_SUBJECT, A.CREATED_AT, FN_USER_NM(A.CREATED_NO) AS CREATED_NAME FROM T_BOARD A WHERE 1 = 1 AND A.BOARD_TP = #{boardTp}
AND A.CUSTOMER_NO = #{customerNo}
AND (BOARD_SUBJECT LIKE CONCAT('%', #{searchKeyword}, '%') OR BOARD_CONTENT LIKE CONCAT('%', #{searchKeyword}, '%'))
AND A.DELETED_FG = 'N') TB

mapper.insert:
INSERT INTO T_BOARD
    (
          CUSTOMER_NO
        , BOARD_TP
        , BOARD_SUBJECT
        , BOARD_CONTENT
        , BOARD_VIEWS
        , CREATED_NO
    ) VALUES (
          #{customerNo}
        , #{boardTp}
        , #{boardSubject}
        , #{boardContent}
        , 0
        , #{createdNo}
    )

mapper.update:
UPDATE T_BOARD
SET
      BOARD_SUBJECT = #{boardSubject}
    , BOARD_CONTENT = #{boardContent}
    , UPDATED_AT = NOW()
WHERE BOARD_NO = #{boardNo}
