mapper.select:
SELECT
    A.BBS_NO, A.BBS_SJ, A.BBS_CN, A.BBS_SE, A.BBS_TP, A.BBS_VIEWS, A.REGISTER_DT, A.BBS_FIX_AT
FROM T_BBS A
WHERE 1=1
  AND A.DELETED_AT = 'N'
  AND A.BBS_SE = #{bbsSe}

    AND A.BBS_TP = #{bbsTp}

ORDER BY REGISTER_DT DESC
LIMIT #{startRow}, #{pageCnt}

mapper.insert:
INSERT INTO T_BBS
    (
          BBS_SJ
        , BBS_CN
        , BBS_SE

        , BBS_TP


        , BBS_FIX_AT

        , REGISTER_NO
    ) VALUES
    (
          #{bbsSj}
        , #{bbsCn}
        , #{bbsSe}

        , #{bbsTp}


        , #{bbsFixAt}

        , #{registerNo}
    )

mapper.update:
UPDATE T_BBS
SET   BBS_SJ = #{bbsSj}
    , BBS_CN = #{bbsCn}

    , BBS_TP = #{bbsTp}

    , UPDATED_DT = NOW()
    , UPDATED_NO = #{registerNo}
WHERE BBS_NO = #{bbsNo}

UPDATE T_BBS
SET   BBS_FIX_AT = #{type}
    , UPDATED_DT = NOW()
WHERE BBS_NO IN

    #{item}
