mapper.insert:
INSERT INTO T_CODE
(
      CODE_NAME
    , CODE_DESC
    , CODE_PARENT_NO
    , CODE_EDIT_YN
    , CODE_ORDR
    , REGISTER_NO
) VALUES (
      #{codeName}
    , #{codeDesc}
    , #{codeParentNo}
    , #{codeEditYn}
    , (SELECT IFNULL(MAX(CODE_ORDR),0)+1 FROM T_CODE A WHERE DELETED_AT = 'N' AND CODE_PARENT_NO = #{codeParentNo})
    , #{registerNo}
)

mapper.update:
UPDATE T_CODE
SET
    CODE_NAME = #{codeName}
  , CODE_DESC = #{codeDesc}
  , CODE_PARENT_NO = #{codeParentNo}
  , CODE_EDIT_YN = #{codeEditYn}
  , UPDATED_AT = NOW()
WHERE CODE_NO = #{codeNo}

UPDATE T_CODE
SET
      CODE_NAME = #{codeName}
    , CODE_DESC = #{codeDesc}
    , UPDATED_AT = NOW()

    , DELETED_AT = NOW()

    , DELETED_FG = #{deletedFg}
WHERE CODE_NO = #{codeNo}

