mapper.select:
SELECT
    COUNT(*)
FROM
    T_MEMBER
WHERE
    1=1
  AND MEMBER_HP = #{phone} AND MEMBER_HP_NATION = #{memberContactNation}

      AND MEMBER_EMAIL = #{email}

  AND MEMBER_TP = #{memberTp}
  AND DELETED_AT = 'N'

mapper.insert:
INSERT INTO T_AUTH_CODE
(
  AUTH_CODE
, TOKEN_VALUE
, MEMBER_CONTACT_NATION
, PHONE_NO
) VALUES (
           #{authCode}
         , #{tokenValue}
         , #{memberContactNation}
         , #{phoneNo}
         )

mapper.update:
UPDATE T_AUTH_CODE
SET AUTH_YN = 'Y'
WHERE AUTH_CODE = #{authCode}
AND TOKEN_VALUE = #{token}

mapper.select:
SELECT
    A.ALARM_NO, A.ALARM_SJ, A.ALARM_CN, A.ALARM_SE, A.ALARM_URL, A.READ_AT, A.MEMBER_NO,
    FN_GET_TIMEZONE_DATE(#{memberNo}, A.REGISTER_DT) AS REGISTER_DT
FROM T_ALARM A
WHERE A.DELETED_AT = 'N'
  AND A.MEMBER_NO = #{memberNo}

    AND A.ALARM_SE = #{alarmSe}


    AND A.READ_AT = 'N'

ORDER BY A.REGISTER_DT DESC
LIMIT #{startRow}, #{pageCnt}

SELECT COUNT(*) AS CNT FROM T_ALARM A WHERE A.DELETED_AT = 'N' AND A.MEMBER_NO = #{memberNo}
AND A.ALARM_SE = #{alarmSe}
AND A.READ_AT = 'N'

mapper.update:
UPDATE T_ALARM
SET READ_AT = 'Y'
WHERE MEMBER_NO = #{memberNo}
 AND ALARM_NO = #{alarmNo}

mapper.insert:
INSERT INTO T_ALARM
    (
          ALARM_SJ
        , ALARM_CN
        , ALARM_SE
        , ALARM_URL
        , MEMBER_NO
    ) VALUES (
          #{alarmSj}
        , #{alarmCn}
        , #{alarmSe}
        , #{alarmUrl}
        , #{memberNo}
    )
