mapper.insert:
INSERT INTO T_FILE
	(
		  FILE_SE
		, FILE_FROM_NO
		, FILE_ORDR
		, FILE_URL
		, FILE_NAME
		, FILE_REAL_NAME
		, FILE_SIZE
		, REGISTER_NO
	) VALUES (
		  #{fileSe}
		, #{fileFromNo}
		, (SELECT IFNULL(MAX(FILE_ORDR),0)+1 FROM T_FILE A WHERE FILE_SE = #{fileSe} AND FILE_FROM_NO = #{fileFromNo})
		, #{fileUrl}
		, #{fileName}
		, #{fileRealName}
		, #{fileSize}
		, #{registerNo}
	)

mapper.select:
SELECT
	FILE_NO, FILE_SE, FILE_FROM_NO, FILE_ORDR, FILE_URL, FILE_NAME, FILE_REAL_NAME, FILE_SIZE
FROM T_FILE
WHERE DELETED_AT = 'N'
  AND FILE_FROM_NO = #{fileFromNo}
  AND FILE_SE = #{fileSe}

mapper.update:
UPDATE T_FILE
SET
	DELETED_AT = 'Y'
  , DELETED_DT = NOW()
WHERE FILE_SE = #{fileSe}
  AND FILE_FROM_NO = #{fileFromNo}

mapper.insert:
INSERT INTO T_FILE
(
  FILE_SE
, FILE_FROM_NO
, FILE_ORDR
, FILE_URL
, FILE_NAME
, FILE_REAL_NAME
, FILE_SIZE
, REGISTER_NO
) VALUES (
		   #{fileSe}
		 , #{fileFromNo}
		 , #{fileOrdr}
		 , #{fileUrl}
		 , #{fileName}
		 , #{fileRealName}
		 , #{fileSize}
		 , #{registerNo}
		 )

mapper.select:
SELECT
	FILE_NO, FILE_SE, FILE_FROM_NO, FILE_ORDR, FILE_URL, FILE_NAME, FILE_REAL_NAME, FILE_SIZE
FROM T_FILE
WHERE DELETED_AT = 'N'
  AND FILE_SE = #{fileSe}
  AND FILE_FROM_NO = #{fileFromNo}
  AND FILE_ORDR = #{fileOrdr}
ORDER BY FILE_ORDR
